package java_interview;

public class swap_two_numbers {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int mysalary=41;
int superior_salary=50;
System.out.println("before swapping"+"mine:"+mysalary+"superior:"+superior_salary);
int temp=mysalary;
mysalary=superior_salary;
superior_salary=temp;
System.out.println("After swapping"+"mine:"+mysalary+"superior:"+superior_salary);
	}

}
